                        "id": "5a96fda57fc8fb601adea2c1",
                        "uuid": "9e7b9933-7ab3-4ebd-99af-cc2a2635e1ea",
                        "title": "Asp.net ReportViewer : Access Remote Server Report with Credentials",
                        "slug": "asp-net-reportviewer-access-remote-server-report-with-credentials",
                        "mobiledoc": "{\"version\":\"0.3.1\",\"markups\":[],\"atoms\":[],\"cards\":[[\"markdown\",{\"cardName\":\"card-markdown\",\"markdown\":\"If you want to work with remote SSRS report while debugging your asp.net application you would want to authenticate with the remote server, you cannot use asp.net impersonation as this would actually make the entire application run under impersonation so to solve this we can use impersonation for the report viewer only to do this :\\n\\n<pre lang=\\\"csharp\\\">\\n#if (DEBUG)\\nthis.ReportViewer1.ServerReport.ReportServerCredentials = new ReportServerCredentials(\\\"username\\\", \\\"password\\\",\\\"domain\\\");\\n#endif\\n</pre>\\n\\nand a <b>ReportServerCredentials</b> class\\n<pre lang=\\\"csharp\\\">\\npublic class ReportServerCredentials : IReportServerCredentials\\n    {\\n        private string _userName;\\n        private string _password;\\n        private string _domain;\\n\\n        public ReportServerCredentials(string userName, string password, string domain)\\n        {\\n            _userName = userName;\\n            _password = password;\\n            _domain = domain;\\n        }\\n\\n        public System.Security.Principal.WindowsIdentity ImpersonationUser\\n        {\\n            get { return null; }\\n        }\\n\\n\\n        public System.Net.ICredentials NetworkCredentials\\n        {\\n            get { return new System.Net.NetworkCredential(_userName, _password, _domain); }\\n        }\\n\\n\\n        bool IReportServerCredentials.GetFormsCredentials(out Cookie authCookie, out string userName, out string password, out string authority)\\n        {\\n            authCookie = null;\\n            userName = _userName;\\n            password = _password\\n            authority = _domain;\\n            return false;\\n        }\\n    }\\n</pre>\"}]],\"sections\":[[10,0]],\"ghostVersion\":\"3.0\"}",
                        "html": "<!--kg-card-begin: markdown--><p>If you want to work with remote SSRS report while debugging your asp.net application you would want to authenticate with the remote server, you cannot use asp.net impersonation as this would actually make the entire application run under impersonation so to solve this we can use impersonation for the report viewer only to do this :</p>\n<pre lang=\"csharp\">\n#if (DEBUG)\nthis.ReportViewer1.ServerReport.ReportServerCredentials = new ReportServerCredentials(\"username\", \"password\",\"domain\");\n#endif\n</pre>\n<p>and a <b>ReportServerCredentials</b> class</p>\n<pre lang=\"csharp\">\npublic class ReportServerCredentials : IReportServerCredentials\n    {\n        private string _userName;\n        private string _password;\n        private string _domain;\n\n        public ReportServerCredentials(string userName, string password, string domain)\n        {\n            _userName = userName;\n            _password = password;\n            _domain = domain;\n        }\n\n        public System.Security.Principal.WindowsIdentity ImpersonationUser\n        {\n            get { return null; }\n        }\n\n\n        public System.Net.ICredentials NetworkCredentials\n        {\n            get { return new System.Net.NetworkCredential(_userName, _password, _domain); }\n        }\n\n\n        bool IReportServerCredentials.GetFormsCredentials(out Cookie authCookie, out string userName, out string password, out string authority)\n        {\n            authCookie = null;\n            userName = _userName;\n            password = _password\n            authority = _domain;\n            return false;\n        }\n    }\n</pre><!--kg-card-end: markdown-->",
                        "comment_id": "111",
                        "plaintext": "If you want to work with remote SSRS report while debugging your asp.net\napplication you would want to authenticate with the remote server, you cannot\nuse asp.net impersonation as this would actually make the entire application run\nunder impersonation so to solve this we can use impersonation for the report\nviewer only to do this :\n\n\n#if (DEBUG)\nthis.ReportViewer1.ServerReport.ReportServerCredentials = new ReportServerCredentials(\"username\", \"password\",\"domain\");\n#endif\n\n\nand a ReportServerCredentials  class\n\n\npublic class ReportServerCredentials : IReportServerCredentials\n    {\n        private string _userName;\n        private string _password;\n        private string _domain;\n\n        public ReportServerCredentials(string userName, string password, string domain)\n        {\n            _userName = userName;\n            _password = password;\n            _domain = domain;\n        }\n\n        public System.Security.Principal.WindowsIdentity ImpersonationUser\n        {\n            get { return null; }\n        }\n\n\n        public System.Net.ICredentials NetworkCredentials\n        {\n            get { return new System.Net.NetworkCredential(_userName, _password, _domain); }\n        }\n\n\n        bool IReportServerCredentials.GetFormsCredentials(out Cookie authCookie, out string userName, out string password, out string authority)\n        {\n            authCookie = null;\n            userName = _userName;\n            password = _password\n            authority = _domain;\n            return false;\n        }\n    }",
